def latlon_to_svg(lat, lon, center_lat=23.7667, center_lon=121.0000, center_x=675, center_y=585, width=1000, height=1000):
    # 經緯度範圍
    min_lon, max_lon = 117, 124
    min_lat, max_lat = 20, 26

    # 每度經緯度的像素
    pixels_per_lon = width / (max_lon - min_lon)  # 1000 / 7 ≈ 142.86
    pixels_per_lat = height / (max_lat - min_lat)  # 1000 / 6 ≈ 166.67

    # 計算相對於中心點的偏移
    delta_lon = lon - center_lon
    delta_lat = lat - center_lat

    # 轉換到 SVG 座標
    x = center_x + (delta_lon * pixels_per_lon)
    y = center_y - (delta_lat * pixels_per_lat)

    return max(0, min(x, width)), max(0, min(y, height))

def is_offshore(lat, lon):
    if (118 <= lon <= 122 and 21.5 <= lat <= 25.5) or \
       (119 <= lon <= 119.8 and 23 <= lat <= 23.8) or \
       (118 <= lon <= 118.5 and 24 <= lat <= 24.5) or \
       (119.8 <= lon <= 120.5 and 26 <= lat <= 26.5):
        return None
    if lon > 122:
        return "太平洋"
    elif lat < 21.5:
        return "菲律賓海"
    elif lon < 118:
        return "台灣海峽"
    elif lat > 25.5:
        return "東海"
    return "外海"

def mag_to_intensity(mag):
    if mag < 0:
        return 0
    elif mag < 4.5:
        return round(mag)
    elif mag < 5:
        return 5
    elif mag < 5.5:
        return 6
    elif mag < 6:
        return 7
    elif mag < 6.5:
        return 8
    else:
        return 9

def draw(eq_data, intensitys: dict):
    id= eq_data["id"]
    serial= eq_data["serial"]
    eq = eq_data["eq"]
    lat = eq["lat"]
    lon = eq["lon"]
    mag = eq["mag"]
    loc = eq["loc"]
    max = eq["max"]
    depth = eq["depth"]
    time = eq["time"]

    # 計算震央座標
    epicenter_x, epicenter_y = latlon_to_svg(lat, lon)

    # 計算震度和顏色
    mag_color = INTENSITY_COLORS.get(mag_to_intensity(mag), DEFAULT_COLOR)

    # 準備 SVG 組件
    svg_parts = []

    # 添加 SVG 頭部
    svg_parts.append(TW_MAP_SVG['head'])

    # 處理每個縣市的 <path> 並設置顏色
    for county_id in COUNTY_CENTERS.keys():
        if county_id in TW_MAP_SVG:
            intensity = intensitys.get(county_id, 0)  # 預設震度為 0
            intensity_color = INTENSITY_COLORS.get(intensity, DEFAULT_COLOR)
            path_content = TW_MAP_SVG[county_id].replace(f'{{{county_id}_COLOR}}', intensity_color)
            svg_parts.append(path_content)

    # 添加震央和文字標記
    offshore_area = is_offshore(lat, lon)
    if offshore_area:
        offshore_zones = {
            "太平洋": f'<rect x="950" y="0" width="50" height="1000" fill="{mag_color}" opacity="0.1"><animate attributeName="opacity" from="0.1" to="0.5" dur="2s" repeatCount="indefinite"/></rect>',
            "菲律賓海": f'<rect x="0" y="950" width="1000" height="50" fill="{mag_color}" opacity="0.1"><animate attributeName="opacity" from="0.1" to="0.5" dur="2s" repeatCount="indefinite"/></rect>',
            "台灣海峽": f'<rect x="0" y="0" width="50" height="1000" fill="{mag_color}" opacity="0.1"><animate attributeName="opacity" from="0.1" to="0.5" dur="2s" repeatCount="indefinite"/></rect>',
            "東海": f'<rect x="0" y="0" width="1000" height="50" fill="{mag_color}" opacity="0.1"><animate attributeName="opacity" from="0.1" to="0.5" dur="2s" repeatCount="indefinite"/></rect>'
        }
        svg_parts.append(offshore_zones.get(offshore_area, offshore_zones["太平洋"]))
    else:
        svg_parts.append(f'<circle cx="{epicenter_x}" cy="{epicenter_y}" r="10" fill="#BE3455"><animate attributeName="r" from="10" to="15" dur="1s" repeatCount="indefinite"/></circle>')

    # 添加地震資訊
    svg_parts.append(TW_MAP_SVG['info'].format(
        id=id,
        serial=serial,
        depth=depth,
        mag=mag,
        max=max,
        time=time,
        loc=loc,
        geoloc="定位中...",
        max_area="定位中..."
    ))

    # 添加圖例和版權信息
    svg_parts.append(TW_MAP_SVG['legend'])
    svg_parts.append(TW_MAP_SVG['copyright'])

    # 添加 SVG 結尾
    svg_parts.append(TW_MAP_SVG['end'])

    # 拼接所有部分
    return "\n".join(svg_parts)